import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
import axios from "axios"
import Navbar from '../Component/Navbar';
import "./All.css"

const Detail = () => {
  let { storeName } = useParams()
  const [detail, setDetail] = useState()

  useEffect(() => {
    const api_Link = `https://www.cheapshark.com/api/1.0/games?title=${storeName}`
    axios.get(api_Link)
      .then(response => {
        setDetail(response.data)
      })

  }, [storeName])

  console.log(detail, "detailll");


  return (
    <>
      <div className='ustnavbar'>
        <div class="text-center">
          <div class="row align-items-start">
            <div class="col">
              One of three columns
            </div>
            <div class="col">
              One of three columns
            </div>
            <div class="col">
              One of three columns
            </div>
          </div>
        </div>
      </div>
      <br />

      <div class="text-center">
        <div class="row align-items-start">
          <div class="col">
            Oyunlar<br /><br />
            <ol>
              {
                detail?.map((liste, i) =>
                  <ol key={i} style={{ textAlign: "left", fontSize: 20 }}>
                    {liste.internalName}

                  </ol>
                )
              }
            </ol>
          </div>
          <div class="col">
            Oyunun Resmi<br /><br />
            <ol>
              {
                detail?.map((resim, i) =>
                  <ol>
                    <img key={i} src={resim.thumb} style={{ width: 100 }} />
                  </ol>
                )
              }
            </ol>
          </div>
          <div class="col">
            Fiyat<br /><br />
            {
              detail?.map((fiyat, i) =>
                <ol key={i}>
                  {fiyat.cheapest}
                </ol>
              )
            }
          </div>
        </div>
      </div>
    </>
  )
}

export default Detail;
